var x = -50;
var y = -50;
var Cx = 50;
var Cy = 50;
var Cd = 25;
var mouseX = 25;
var mouseY = 25;
var w = 87;
var s = 83;
var a = 65;
var d = 68;
var BorderThickness = 5;
var obstaclex = 400;
var obstacley = 300;
var speedx = 2;
var speedy = 2;

function setup() {
  createCanvas(800, 600);
  
}


function draw() 
{
  background(100,58,80);
  
  fill(300);
  circle(Cx, Cy, Cd);

  controlCircle();

  fill(100,90,10)
  ellipse(x, y, 15, 50);

  createBorders(10);
  
  textSize(20);
  text("EXIT", 700,500)
  fill(0)
  circle(722,530,50)

if (Cx > 700 && Cx < 750 && Cy > 500 && Cy < 560)
  {
  textSize(30);
  text("YOU WIN!!!", 300,300)
  }
  
  fill(200,50,10)
  ellipse(obstaclex,obstacley,15,50)

  myObstacle();
 
}
function mousePressed()
{
  x = mouseX;
  y = mouseY;
}
function createBorders()
{
  rect(0,0,width,BorderThickness);
  rect(0,0,BorderThickness,height);
  rect(0,height - BorderThickness,width,BorderThickness);
  rect(width - BorderThickness,0,BorderThickness,height);
}
function myObstacle()
{
  obstaclex += speedx;
  obstacley += speedy;

  speedx = random(-5,5);
  speedy = random(-5,5);
}
function controlCircle() 
{
  if (Cx >= 800) 
{
  Cy = 50;
  Cx = 50;
}
if (Cx <= 5) 
  {
    Cy = 50;
    Cx = 50;
  }

if (Cy >= 600) 
{
 Cy = 50;
 Cx = 50;
}
if (Cy <= 5) 
  {
    Cy = 50;
    Cx = 50;
  }
 if (keyIsDown(w)) { 
    Cy -= 5;
  }
  if (keyIsDown(s)) { 
    Cy += 5;
  }
  if (keyIsDown(a)) { 
    Cx -= 5;
  }
  if (keyIsDown(d)) {
    Cx += 5;
  }


}